export default function createCards(projectList,section){let projectDiv="";for(let i=0;i<projectList.demoLinks.length;i++)projectDiv+=` <section class="card">\n  <div class="card-img-container">\n  <a\n  class="blog-link"\n  href=${projectList.demoLinks[i]}\n  target="_blank"\n  >\n  ${projectList.images[i].endsWith("webm")?`<video class='skeleton' autoplay loop>\n  <source src=${projectList.images[i]} type="video/webm">\n  </video>`:`<img class='skeleton' src=${projectList.images[i]} alt=${projectList.names[i]}/>`}\n</a>\n</div>\n\n<div class="img-description card-desc">\n<p class="project-name">${projectList.names[i]}</p> \n  <p class="project-desc">\n  ${projectList.tech[i]}\n  </p>\n  \n  <div class='card-button-container'>\n  <button class='card-button'>\n  <a href='${projectList.githubLinks[i]}' target="_blank" class='demo-btn'>\n  <i class="fa fa-github-square" aria-hidden="true"></i>\n  Code\n  </a>\n  </button>  \n  <button class='card-button'>\n  <a href='${projectList.demoLinks[i]}'target="_blank" class='demo-btn'>\n  Live\n  <i class="fas fa-external-link-alt" aria-hidden="true"></i>\n  </a>\n  </button>\n  </div>\n  </div>\n  \n</section>\n`;section.innerHTML+=projectDiv}