export default function createCards(projectList,section){let projectDiv="";for(let i=0;i<projectList.demoLinks.length;i++)projectDiv+=` <div class="card">\n    ${projectList.images[i].endsWith("webm")?`<video class='skeleton' autoplay loop>\n    <source src=${projectList.images[i]} type="video/webm">\n    </video>`:`<img class='skeleton' src=${projectList.images[i]} alt=${projectList.names[i]}/>`}\n\n    <div class="card-text">\n    <h2>${projectList.names[i]}</h2> \n    <p>${projectList.tech[i]}</p>\n      <div class="card-btn-container">\n        <a href='${projectList.githubLinks[i]}' target="_blank">\n          <i class="fa fa-github-square" aria-hidden="true"></i>\n          Code\n        </a>\n        <a href='${projectList.demoLinks[i]}'target="_blank">\n          Live\n          <i class="fas fa-external-link-alt" aria-hidden="true"></i>\n        </a>\n      </div>\n    </div>\n  </div>\n`;section.innerHTML+=projectDiv}